Http.request({ method: 'POST', url: `https://app.zipboard.co/api/v1/shareurl`, body: { "projectid": "h7JGWE5Rcd3coiFkY", "fileid":"Nzjv8Zj3CYki83s8E", "type":"viewonly", "secure":false }, }); Response { "status": "Success", "status code": 201, "data": [ { "fileId": "Nzjv8Zj3CYki83s8E", "projectId": "h7JGWE5Rcd3coiFkY", "linkAccess": "shareviewonly", "linkTokenId": "73b1c69413bb3371c0529666", "shareLink": "http://localhost:3000/s/2Sp43", "loginRequired": false, "clicks": 0, "message": "In this url user can view previous tasks and boards." } ]
